controller = "sample";

$(document).ready(function() {
    console.log("document Ready");
    $("#processingWrapper").css("display", "none");
    $("#postButton").off().on("click", function() {
        aDataPrepFunction();
    });
    $("#getButton").off().on("click", function() {
        console.log("event fired");
        aDataPrepFunction2();
    });
});

function aDataPrepFunction() {
    var n = "value1";
    var o = {
        data1: n,
        data2: 3,
        data3: true
    };
    sampleController("POST", o, "/sampleaction1");
}

function aDataPrepFunction2() {
    console.log("data preped");
    var n = "value1";
    var o = {
        data1: n,
        data2: 3,
        data3: true
    };
    sampleController("GET", o, "/sampleaction2");
}

function sampleController(n, o, e) {
    console.log("in controller");
    $.ajax({
        type: n,
        data: o,
        url: controller + e,
        dataType: "json",
        beforeSend: function() {
            $("#processingWrapper").css("display", "block");
        },
        complete: function() {
            $("#processingWrapper").css("display", "none");
        },
        success: function(n) {
            responseHandler(e, n);
        },
        error: function(n, o, e) {
            $("#processingWrapper").hide();
            $("#wrapper").css("display", "inline-block");
            $("#notification").css("display", "inline-block");
            $("#notification").text("sampleController() failed! " + "Error: " + o + ". HTTP status: " + e + ".");
            $("#notification").removeClass("greenback");
            $("#notification").addClass("redback");
        }
    });
}

function responseHandler(n, o) {
    console.log("in Handler");
    if (n == "/sampleaction1") {
        sampleAction1Response(o);
    } else if (n == "/sampleaction2") {
        sampleAction2Response(o);
    } else {
        $("#notification").css("display", "inline-block");
        $("#notification").text("Something went wrong...");
        $("#notification").removeClass("greenback");
        $("#notification").addClass("redback");
    }
}

function sampleAction1Response(n) {
    console.log("in sampleAction1Response");
    alert(n);
}

function sampleAction2Response(n) {
    console.log("in sampleAction2Response");
    alert(n);
}